import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import api from '../api';
import SkeletonTable from './SkeletonTable';
import './CSVViewer.css';
import { useTheme } from '../contexts/ThemeContext';

export default function CSVViewer() {
  const { id }         = useParams();
  const navigate        = useNavigate();
  const { logout }      = useAuth();
const { theme, toggleTheme } = useTheme();

  const [data, setData]       = useState([]);
  const [headers, setHeaders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get(`/files/${id}/data`)
      .then(res => {
        const rows = res.data;
        setData(rows);
        if (rows.length) setHeaders(Object.keys(rows[0]));
      })
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) {
    // skeleton: rows=5, cols = headers length fallback to 3
    return (
      <div className="csv-viewer">
        <SkeletonTable rows={5} cols={headers.length || 3} />
      </div>
    );
  }

  const handleBack = () => navigate('/dashboard');
  const handleLogout = async () => { await logout(); navigate('/login'); };

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={handleBack}>‚Üê Back</button>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </div>
           <div className="csv-header">
       <button className="back-btn" onClick={handleBack}>‚Üê Back</button>
       <div>
         <button className="theme-btn" onClick={toggleTheme}>
           {theme === 'light' ? 'üåô Dark' : '‚òÄÔ∏è Light'}
         </button>
         <button className="logout-btn" onClick={handleLogout}>Logout</button>
       </div>
     </div>
      {data.length ? (
        <table className="data-table">
          <thead><tr>{headers.map(h => <th key={h}>{h}</th>)}</tr></thead>
          <tbody>
            {data.map((row,i) => (
              <tr key={i}>{headers.map(h => <td key={h}>{row[h]}</td>)}</tr>
            ))}
          </tbody>
        </table>
      ) : <p className="no-data">No data to display.</p>}
    </div>
  );
}
