import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import api from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id: fileId } = useParams();
  const navigate        = useNavigate();
  const { logout }      = useAuth();
  const { addToast }    = useToast();

  const [rows, setRows]             = useState([]);
  const [columns, setColumns]       = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredRows, setFilteredRows] = useState([]);
  const [loading, setLoading]       = useState(true);

  // Fetch data once
  useEffect(() => {
    setLoading(true);
    api.get(`/files/${fileId}/data`)
      .then(res => {
        const data = res.data;         // e.g. [ { _rowId, colA: '...', colB: '...' }, ... ]
        setRows(data);
        setFilteredRows(data);
        if (data.length) {
          setColumns(Object.keys(data[0]).filter(k => k !== '_rowId'));
        }
      })
      .catch(() => addToast('Failed to load data', 'error'))
      .finally(() => setLoading(false));
  }, [fileId, addToast]);

  // Filter whenever searchQuery changes
  useEffect(() => {
    if (!searchQuery) {
      setFilteredRows(rows);
    } else {
      const q = searchQuery.toLowerCase();
      setFilteredRows(
        rows.filter(row =>
          columns.some(col =>
            String(row[col] ?? '')
              .toLowerCase()
              .includes(q)
          )
        )
      );
    }
  }, [searchQuery, rows, columns]);

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  if (loading) {
    return <div className="csv-viewer"><p>Loading…</p></div>;
  }

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={() => navigate('/dashboard')}>
          ← Back
        </button>
        <input
          className="search-input"
          type="text"
          placeholder="Search..."
          value={searchQuery}
          onChange={e => setSearchQuery(e.target.value)}
        />
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      <div className="table-wrap">
        <table className="data-table">
          <thead>
            <tr>
              {columns.map(col => (
                <th key={col}>{col}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredRows.map(row => (
              <tr key={row._rowId}>
                {columns.map(col => (
                  <td key={col}>{row[col]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
