import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate, useParams }  from 'react-router-dom';  //useParams: Hook to extract route parameters from the URL
import { useAuth } from '../contexts/AuthContext'; //custom hook
import { useToast } from '../contexts/ToastContext'; //custom hook
import api  from '../api'; //A configured Axios instance (in your api.js) for sending HTTP requests to backend (/api/...) with cookies.
import './CSVViewer.css';

export default function CSVViewer() {
  const { id } = useParams();
  const nav= useNavigate();
  const { logout } = useAuth();
  const { addToast } = useToast();

  const [data, setData] = useState([]);    // rows with _rowId
  const [cols, setCols] = useState([]);    // column names When new column is added, setCols([...cols, newCol]) re-renders the table.
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  // Load data
  useEffect(() => {
    setLoading(true);
    api.get(`/files/${id}/data`)
      .then(res => {
        setData(res.data);
        if (res.data.length) {
          setCols(Object.keys(res.data[0]).filter(k => k !== '_rowId'));
        }
      })
      .catch(() => addToast('Failed to load data', 'error'))
      .finally(() => setLoading(false));
  }, [id, addToast]);

  // Filtered rows
  const filtered = useMemo(() => {
    const q = searchTerm.trim().toLowerCase();
    if (!q) return data;
    return data.filter(r =>
      cols.some(c =>
        String(r[c] ?? '').toLowerCase().includes(q)
      )
    );
  }, [searchTerm, data, cols]);

  const handleEdit = (rowId, col, val) => {
    setData(d =>
      d.map(r =>
        r._rowId === rowId
          ? { ...r, [col]: val, _isEdited: true }
          : r
      )
    );
  };

  // Add new column: mark all rows as edited so blank values persist
  const handleAddColumn = () => {
    const name = prompt('Enter new column name:');
    if (!name || cols.includes(name)) return;
    setCols(prev => [...prev, name]);
    setData(prev =>
      prev.map(r => ({
        ...r,
        [name]: '',
        _isEdited: true   // ensure this row gets PUT to backend
      }))
    );
  };

  // Add new row locally — persisted on Save
  const handleAddRow = () => {
    const tempId = `new_${Date.now()}`;
    const empty  = cols.reduce((o, c) => ({ ...o, [c]: '' }), {});
    setData(prev => [
      ...prev,
      { _rowId: tempId, ...empty, _isNew: true }
    ]);
  };

  const handleSave = async () => {
    // 1) POST new rows
    for (const r of data.filter(r => r._isNew)) {
      const { _rowId, _isNew, _isEdited, ...rowData } = r;
      try {
        const res = await api.post(`/files/${id}/data`, { rowData });
        r._rowId = res.data.id;       // replace tempId
        delete r._isNew;
      } catch {
        return addToast('Failed to add new row', 'error');
      }
    }

    // 2) PUT edited rows (includes those from handleAddColumn)
    const edits = data.filter(r => !r._isNew && r._isEdited);
    if (edits.length) {
      const updates = edits.map(r => {
        const { _rowId, _isEdited, ...rowData } = r;
        return { id: _rowId, rowData };
      });
      try {
        await api.put(`/files/${id}/data`, { updates });
      } catch {
        return addToast('Failed to save edits', 'error');
      }
    }

    // 3) Clear flags
    setData(prev =>
      prev.map(r => {
        const nr = { ...r };
        delete nr._isEdited;
        return nr;
      })
    );
    addToast('Changes saved!', 'success');
  };

  const handleDownload = () => {
    const lines = [ cols.join(',') ];
    filtered.forEach(r =>
      lines.push(
        cols.map(c => {
          let v = r[c] ?? '';
          if (typeof v === 'string') v = `"${v.replace(/"/g,'""')}"`;
          return v;
        }).join(',')
      )
    );
    const blob = new Blob([lines.join('\n')], { type: 'text/csv' });
    const url  = URL.createObjectURL(blob);
    const a    = document.createElement('a');
    a.href     = url;
    a.download = `export-${id}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  if (loading) return <div className="csv-viewer"><p>Loading…</p></div>;

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={() => nav('/dashboard')}>
          ← Back
        </button>
        <input
          className="search-input"
          placeholder="Search…"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        <div className="action-buttons">
          <button className="add-column-btn" onClick={handleAddColumn}>
            + Column
          </button>
          <button className="add-row-btn" onClick={handleAddRow}>
            + Row
          </button>
          <button className="download-btn" onClick={handleDownload}>
            Download CSV
          </button>
          <button className="save-btn" onClick={handleSave}>
            Save Changes
          </button>
          <button
  className="collab-btn"
  onClick={async () => {
    const email = prompt('Collaborator email:');
    if (!email) return;
    try {
      await api.post(`/files/${id}/collaborate`, { email });
      addToast('Request sent!', 'success');
    } catch (err) {
      addToast(err.response?.data?.error || 'Failed', 'error');
    }
  }}
>
  Collaborate
</button>

          <button
            className="logout-btn"
            onClick={() => { logout(); nav('/login'); }}
          >
            Logout
          </button>
        </div>
      </div>

      <div className="table-wrap">
        <table className="data-table">
          <thead>
             <tr>{cols.map(c => <th key={c}>{c}</th>)}</tr> {/* <tr><th>Name</th><th>Email</th></tr> */}

          </thead>
          <tbody>
            {filtered.length > 0 ? filtered.map(r => (
              <tr key={r._rowId} className={r._isEdited ? 'edited-row' : ''}>
                {cols.map(col => (
                  <td key={col}>
                    <input
                      className="edit-input"
                      value={r[col] ?? ''}
                      onChange={e => handleEdit(r._rowId, col, e.target.value)}
                    />
                  </td>
                ))}
              </tr>
            )) : (
              <tr>
                <td colSpan={cols.length} className="no-data">
                  No matching rows
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
