import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import api from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id } = useParams();
  const nav = useNavigate();
  const { logout } = useAuth();
  const { addToast } = useToast();

  const [data, setData] = useState([]);      // rows with _rowId + csv columns
  const [cols, setCols] = useState([]);      // only user CSV columns
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get(`/files/${id}/data`)
      .then(res => {
        const rows = res.data;
        setData(rows);
        if (rows.length) {
          // all keys except our reserved _rowId
          setCols(Object.keys(rows[0]).filter(k => k !== '_rowId'));
        }
      })
      .catch(() => addToast('Failed to load data','error'))
      .finally(() => setLoading(false));
  }, [id, addToast]);

  const handleEdit = (rowId, col, val) => {
    setData(d =>
      d.map(r => (r._rowId === rowId ? { ...r, [col]: val } : r))
    );
  };

  const handleSave = async () => {
    const updates = data.map(r => {
      const { _rowId, ...rowData } = r;
      return { id: _rowId, rowData };
    });
    try {
      await api.put(`/files/${id}/data`, { updates });
      addToast('Changes saved!', 'success');
    } catch {
      addToast('Save failed.', 'error');
    }
  };

  if (loading) return <div className="csv-viewer"><p>Loading…</p></div>;

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={() => nav('/dashboard')}>← Back</button>
        <button className="save-btn" onClick={handleSave}>Save Changes</button>
        <button className="logout-btn" onClick={() => { logout(); nav('/login'); }}>Logout</button>
      </div>

      <table className="data-table">
        <thead>
          <tr>{cols.map(c => <th key={c}>{c}</th>)}</tr>
        </thead>
        <tbody>
          {data.map(row => (
            <tr key={row._rowId}>
              {cols.map(col => (
                <td key={col}>
                  <input
                    className="edit-input"
                    value={row[col] ?? ''}
                    onChange={e => handleEdit(row._rowId, col, e.target.value)}
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
