import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate, useParams }     from 'react-router-dom';
import { useAuth }                    from '../contexts/AuthContext';
import { useToast }                   from '../contexts/ToastContext';
import api                             from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id }        = useParams();
  const nav           = useNavigate();
  const { logout }    = useAuth();
  const { addToast }  = useToast();

  const [data, setData]             = useState([]);
  const [cols, setCols]             = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading]       = useState(true);
  const [deletedCols, setDeletedCols] = useState([]);
  const [deletedRows, setDeletedRows] = useState([]);

  // Load CSV content
  useEffect(() => {
    setLoading(true);
    api.get(`/files/${id}/data`)
      .then(res => {
        setData(res.data);
        if (res.data.length) {
          setCols(Object.keys(res.data[0]).filter(k => k !== '_rowId'));
        }
      })
      .catch(() => addToast('Failed to load data','error'))
      .finally(() => setLoading(false));
  }, [id, addToast]);

  // Filter based on searchTerm
  const filtered = useMemo(() => {
    const q = searchTerm.trim().toLowerCase();
    if (!q) return data;
    return data.filter(r =>
      cols.some(c =>
        String(r[c] ?? '').toLowerCase().includes(q)
      )
    );
  }, [searchTerm, data, cols]);

  // Edit a cell
  const handleEdit = (rowId, col, val) => {
    setData(d =>
      d.map(r =>
        r._rowId === rowId ? { ...r, [col]: val, _isEdited: true } : r
      )
    );
  };

  // Add column
  const handleAddColumn = () => {
    const name = prompt('Enter new column name:');
    if (!name || cols.includes(name)) return;
    setCols(prev => [...prev, name]);
    setData(prev =>
      prev.map(r => ({ ...r, [name]: '', _isEdited: true }))
    );
  };

  // Delete column
  const handleDeleteColumn = (col) => {
    if (!window.confirm(`Delete column "${col}"?`)) return;
    setCols(c => c.filter(h => h !== col));
    setData(d =>
      d.map(r => {
        const { [col]: _, ...rest } = r;
        return { ...rest, _isEdited: true };
      })
    );
    setDeletedCols(dc => [...dc, col]);
  };

  // Add row
  const handleAddRow = () => {
    const tempId = `new_${Date.now()}`;
    const empty  = cols.reduce((o, c) => ({ ...o, [c]: '' }), {});
    setData(prev => [...prev, { _rowId: tempId, ...empty, _isNew: true }]);
  };

  // Delete row
  const handleDeleteRow = (rowId) => {
    if (!window.confirm('Delete this row?')) return;
    setData(prev => prev.filter(r => r._rowId !== rowId));
    // only track if it exists on server
    if (!String(rowId).startsWith('new_')) {
      setDeletedRows(dr => [...dr, rowId]);
    }
  };

  // Save changes: new rows, updates, deleted columns, deleted rows
  const handleSave = async () => {
    // 1) create new rows
    for (const r of data.filter(r => r._isNew)) {
      const { _rowId, _isNew, _isEdited, ...rowData } = r;
      try {
        const res = await api.post(`/files/${id}/data`, { rowData });
        r._rowId = res.data.id;
        delete r._isNew;
      } catch {
        return addToast('Failed to add new row','error');
      }
    }

    // 2) build updates payload
    const updates = data
      .filter(r => !r._isNew && r._isEdited)
      .map(r => {
        const { _rowId, _isEdited, ...rowData } = r;
        return { id: _rowId, rowData };
      });

    // 3) send everything in one PUT
    try {
      await api.put(`/files/${id}/data`, {
        updates,
        deletedColumns: deletedCols,
        deletedRows
      });
    } catch {
      return addToast('Failed to save changes','error');
    }

    // 4) clear flags
    setData(d => d.map(r => { const nr = { ...r }; delete nr._isEdited; return nr; }));
    setDeletedCols([]);
    setDeletedRows([]);
    addToast('Changes saved!','success');
  };

  // Download CSV
  const handleDownload = () => {
    const lines = [ cols.join(',') ];
    filtered.forEach(r =>
      lines.push(
        cols.map(c => {
          let v = r[c] ?? '';
          if (typeof v === 'string') v = `"${v.replace(/"/g,'""')}"`;
          return v;
        }).join(',')
      )
    );
    const blob = new Blob([lines.join('\n')], { type: 'text/csv' });
    const url  = URL.createObjectURL(blob);
    const a    = document.createElement('a');
    a.href     = url;
    a.download = `export-${id}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  if (loading) return <div className="csv-viewer"><p>Loading‚Ä¶</p></div>;

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={() => nav('/dashboard')}>‚Üê Back</button>
        <input
          className="search-input"
          placeholder="Search‚Ä¶"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        <div className="action-buttons">
          <button className="add-column-btn" onClick={handleAddColumn}>+ Column</button>
          <button className="add-row-btn" onClick={handleAddRow}>+ Row</button>
          <button className="download-btn" onClick={handleDownload}>Download CSV</button>
          <button className="save-btn" onClick={handleSave}>Save Changes</button>
          <button className="collab-btn" onClick={async () => {
            const email = prompt('Collaborator email:');
            if (!email) return;
            try {
              await api.post(`/files/${id}/collaborate`, { email });
              addToast('Request sent!','success');
            } catch (err) {
              addToast(err.response?.data?.error||'Failed','error');
            }
          }}>Collaborate</button>
          <button className="logout-btn" onClick={() => { logout(); nav('/login'); }}>Logout</button>
        </div>
      </div>

      <div className="table-wrap">
        <table className="data-table">
          <thead>
            <tr>
              {cols.map(c => (
                <th key={c}>
                  {c}
                  <button
                    className="del-col-btn"
                    onClick={() => handleDeleteColumn(c)}
                    title="Delete column"
                  >‚úñ</button>
                </th>
              ))}
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filtered.length > 0 ? filtered.map(r => (
              <tr key={r._rowId} className={r._isEdited ? 'edited-row' : ''}>
                {cols.map(col => (
                  <td key={col}>
                    <input
                      className="edit-input"
                      value={r[col] ?? ''}
                      onChange={e => handleEdit(r._rowId, col, e.target.value)}
                    />
                  </td>
                ))}
                <td>
                  <button
                    className="del-row-btn"
                    onClick={() => handleDeleteRow(r._rowId)}
                    title="Delete row"
                  >üóëÔ∏è</button>
                </td>
              </tr>
            )) : (
              <tr>
                <td colSpan={cols.length + 1} className="no-data">
                  No matching rows
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
