import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate, useParams }  from 'react-router-dom';
import { useAuth }    from '../contexts/AuthContext';
import { useToast }   from '../contexts/ToastContext';
import api            from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id }        = useParams();
  const nav           = useNavigate();
  const { logout }    = useAuth();
  const { addToast }  = useToast();

  const [data, setData]           = useState([]);
  const [cols, setCols]           = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading]     = useState(true);
  const [deletedCols, setDeletedCols] = useState([]);

  // Load CSV rows
  useEffect(() => {
    setLoading(true);
    api.get(`/files/${id}/data`)
      .then(res => {
        setData(res.data);
        if (res.data.length) {
          setCols(Object.keys(res.data[0]).filter(k => k !== '_rowId'));
        }
      })
      .catch(() => addToast('Failed to load data', 'error'))
      .finally(() => setLoading(false));
  }, [id, addToast]);

  // Filter by search
  const filtered = useMemo(() => {
    const q = searchTerm.trim().toLowerCase();
    if (!q) return data;
    return data.filter(r =>
      cols.some(c =>
        String(r[c] ?? '').toLowerCase().includes(q)
      )
    );
  }, [searchTerm, data, cols]);

  const handleEdit = (rowId, col, val) => {
    setData(d =>
      d.map(r =>
        r._rowId === rowId ? { ...r, [col]: val, _isEdited: true } : r
      )
    );
  };

  const handleAddColumn = () => {
    const name = prompt('Enter new column name:');
    if (!name || cols.includes(name)) return;
    setCols(prev => [...prev, name]);
    setData(prev =>
      prev.map(r => ({ ...r, [name]: '', _isEdited: true }))
    );
  };

  const handleDeleteColumn = (col) => {
    if (!window.confirm(`Delete column "${col}"?`)) return;
    setCols(c => c.filter(h => h !== col));
    setData(d =>
      d.map(r => {
        const { [col]: _, ...rest } = r;
        return { ...rest, _isEdited: true };
      })
    );
    setDeletedCols(dc => [...dc, col]);
  };

  const handleAddRow = () => {
    const tempId = `new_${Date.now()}`;
    const empty  = cols.reduce((o, c) => ({ ...o, [c]: '' }), {});
    setData(prev => [...prev, { _rowId: tempId, ...empty, _isNew: true }]);
  };

  const handleSave = async () => {
    // Prepare payload
    const updates = data
      .filter(r => !r._isNew && r._isEdited)
      .map(r => {
        const { _rowId, _isEdited, ...rowData } = r;
        return { id: _rowId, rowData };
      });
    try {
      await api.put(`/files/${id}/data`, {
        updates,
        deletedColumns: deletedCols
      });
      // Clear flags
      setData(d => d.map(r => { const nr = { ...r }; delete nr._isEdited; return nr; }));
      setDeletedCols([]);
      addToast('Changes saved!', 'success');
    } catch {
      addToast('Failed to save changes', 'error');
    }
  };

  const handleDownload = () => {
    const lines = [cols.join(',')];
    filtered.forEach(r =>
      lines.push(
        cols.map(c => {
          let v = r[c] ?? '';
          if (typeof v === 'string') v = `"${v.replace(/"/g,'""')}"`;
          return v;
        }).join(',')
      )
    );
    const blob = new Blob([lines.join('\n')], { type: 'text/csv' });
    const url  = URL.createObjectURL(blob);
    const a    = document.createElement('a');
    a.href     = url;
    a.download = `export-${id}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  if (loading) return <div className="csv-viewer"><p>Loading…</p></div>;

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={() => nav('/dashboard')}>← Back</button>
        <input
          className="search-input"
          placeholder="Search…"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        <div className="action-buttons">
          <button className="add-column-btn" onClick={handleAddColumn}>+ Column</button>
          <button className="add-row-btn" onClick={handleAddRow}>+ Row</button>
          <button className="download-btn" onClick={handleDownload}>Download CSV</button>
          <button className="save-btn" onClick={handleSave}>Save Changes</button>
          <button
            className="collab-btn"
            onClick={async () => {
              const email = prompt('Collaborator email:');
              if (!email) return;
              try {
                await api.post(`/files/${id}/collaborate`, { email });
                addToast('Request sent!', 'success');
              } catch (err) {
                addToast(err.response?.data?.error || 'Failed', 'error');
              }
            }}
          >
            Collaborate
          </button>
          <button className="logout-btn" onClick={() => { logout(); nav('/login'); }}>
            Logout
          </button>
        </div>
      </div>

      <div className="table-wrap">
        <table className="data-table">
          <thead>
            <tr>
              {cols.map(c => (
                <th key={c}>
                  {c}
                  <button
                    className="del-col-btn"
                    onClick={() => handleDeleteColumn(c)}
                    title="Delete column"
                  >✖</button>
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filtered.length > 0
              ? filtered.map(r => (
                  <tr key={r._rowId} className={r._isEdited ? 'edited-row' : ''}>
                    {cols.map(col => (
                      <td key={col}>
                        <input
                          className="edit-input"
                          value={r[col] ?? ''}
                          onChange={e => handleEdit(r._rowId, col, e.target.value)}
                        />
                      </td>
                    ))}
                  </tr>
                ))
              : (
                <tr>
                  <td colSpan={cols.length} className="no-data">
                    No matching rows
                  </td>
                </tr>
              )
            }
          </tbody>
        </table>
      </div>
    </div>
  );
}
