// frontend/src/components/CSVViewer.jsx
import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import api from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id: fileId } = useParams();
  const nav            = useNavigate();
  const { logout }     = useAuth();
  const { addToast }   = useToast();

  const [rows, setRows]       = useState([]);    // [{ id, colA, colB, … }]
  const [headers, setHeaders] = useState([]);    // ['colA','colB',…]
  const [loading, setLoading] = useState(true);

  // Fetch function
  const fetchData = useCallback(async () => {
    setLoading(true);
    try {
      const res = await api.get(`/files/${fileId}/data`);
      // res.data = [{id,row_data:{…}},…]
      const formatted = res.data.map(r => ({ id: r.id, ...r.row_data }));
      setRows(formatted);
      if (formatted.length) {
        setHeaders(Object.keys(formatted[0]).filter(k => k !== 'id'));
      }
    } catch {
      addToast('Failed to load data', 'error');
    } finally {
      setLoading(false);
    }
  }, [fileId, addToast]);

  // initial + on fileId change
  useEffect(() => { fetchData(); }, [fetchData]);

  // Handle inline edits
  const onCellChange = (rowIdx, col) => e => {
    const text = e.target.innerText;
    setRows(rs => {
      const copy = [...rs];
      copy[rowIdx] = { ...copy[rowIdx], [col]: text };
      return copy;
    });
  };

  // Save & re-fetch
  const saveChanges = async () => {
    try {
      const payload = rows.map(r => {
        const { id, ...data } = r;
        return { id, row_data: data };
      });
      await api.put(`/files/${fileId}/data`, { rows: payload });
      addToast('Changes saved!', 'success');
      await fetchData();
    } catch {
      addToast('Save failed', 'error');
    }
  };

  if (loading) {
    return <p className="no-data">Loading…</p>;
  }

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={() => nav('/dashboard')}>
          ← Back
        </button>
        <div>
          <button className="save-btn" onClick={saveChanges}>
            Save Changes
          </button>
          <button
            className="logout-btn"
            onClick={async () => { await logout(); nav('/login'); }}
          >
            Logout
          </button>
        </div>
      </div>

      {rows.length ? (
        <table className="data-table">
          <thead>
            <tr>{headers.map(h => <th key={h}>{h}</th>)}</tr>
          </thead>
          <tbody>
            {rows.map((row, i) => (
              <tr key={row.id}>
                {headers.map(col => (
                  <td
                    key={col}
                    contentEditable
                    suppressContentEditableWarning
                    onBlur={onCellChange(i, col)}
                  >
                    {row[col]}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="no-data">No data to display.</p>
      )}
    </div>
  );
}
