// frontend/src/components/CSVViewer.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import api from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { logout } = useAuth();

  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [editedRows, setEditedRows] = useState({}); // track changes by rowId
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  // Fetch data on mount
  useEffect(() => {
    api.get(`/files/${id}/data`)
      .then(res => {
        setData(res.data);
        if (res.data.length) setHeaders(Object.keys(res.data[0]).filter(h => h !== 'id'));
      })
      .finally(() => setLoading(false));
  }, [id]);

  // Handle cell edits locally
  const handleCellChange = (rowId, key, value) => {
    setData(prev => prev.map(r => r.id === rowId ? { ...r, [key]: value } : r));
    setEditedRows(prev => ({ ...prev, [rowId]: true }));
  };

  // Save all edited rows
  const handleSave = async () => {
    setSaving(true);
    const rowIds = Object.keys(editedRows);
    try {
      for (const rowId of rowIds) {
        const row = data.find(r => r.id === Number(rowId));
        if (row) {
          await api.put(`/files/${id}/data/${rowId}`, row);
        }
      }
      setEditedRows({});
      // Optionally show a toast here
    } catch (err) {
      console.error(err);
      // Optionally show error toast
    } finally {
      setSaving(false);
    }
  };

  const handleBack = () => navigate('/dashboard');
  const handleLogout = async () => { await logout(); navigate('/login'); };

  if (loading) {
    return <div className="csv-viewer"><p className="no-data">Loading data...</p></div>;
  }

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={handleBack}>‚Üê Back</button>
        <div className="action-buttons">
          <button
            className="save-btn"
            onClick={handleSave}
            disabled={!Object.keys(editedRows).length || saving}
          >
            {saving ? 'Saving...' : 'Save Changes'}
          </button>
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </div>
      </div>

      {data.length ? (
        <table className="data-table">
          <thead>
            <tr>
              {headers.map(h => <th key={h}>{h}</th>)}
            </tr>
          </thead>
          <tbody>
            {data.map(row => (
              <tr key={row.id} className={editedRows[row.id] ? 'edited-row' : ''}>
                {headers.map(col => (
                  <td
                    key={col}
                    contentEditable
                    suppressContentEditableWarning
                    onBlur={e => handleCellChange(row.id, col, e.target.textContent)}
                  >
                    {row[col]}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="no-data">No data to display.</p>
      )}
    </div>
  );
}
