import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import api from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id } = useParams();
  const nav = useNavigate();
  const { logout } = useAuth();
  const { addToast } = useToast();

  const [data, setData] = useState([]);        // each row has _rowId + CSV columns
  const [cols, setCols] = useState([]);     
  const [search, setSearch] = useState('');    // raw search text
  const [filtered, setFiltered] = useState([]);// rows after filter   // only CSV column names
  const [fileName, setFileName] = useState('export');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    Promise.all([
      api.get(`/files/${id}/data`),
      api.get('/files')
    ])
    .then(([dataRes, filesRes]) => {
      const rows = dataRes.data;
      setData(rows);
      if (rows.length) {
        setCols(Object.keys(rows[0]).filter(k => k !== '_rowId'));
      }
      const meta = filesRes.data.find(f => f.id === Number(id));
      if (meta) setFileName(meta.filename.replace(/\.[^.]+$/, ''));
    })
    .catch(() => addToast('Failed to load data', 'error'))
    .finally(() => setLoading(false));
  }, [id, addToast]);

    // Debounced filter effect
  useEffect(() => {    const handle = setTimeout(() => {
      const q = search.toLowerCase().trim();
      if (!q) {
        setFiltered(data);
      } else {
        setFiltered(data.filter(row =>
          cols.some(col =>
            String(row[col] ?? '').toLowerCase().includes(q)
        )
      ));
      }
    }, 300);
    return () => clearTimeout(handle);
  }, [search, data, cols]);

  const handleEdit = (rowId, col, val) => {
    setData(d => d.map(r => r._rowId === rowId ? { ...r, [col]: val } : r));
  };

  const handleSave = async () => {
    const updates = data.map(r => {
      const { _rowId, ...rowData } = r;
      return { id: _rowId, rowData };
    });
    try {
      await api.put(`/files/${id}/data`, { updates });
      addToast('Changes saved!', 'success');
    } catch {
      addToast('Save failed.', 'error');
    }
  };

  const handleDownload = () => {
    // build CSV string
    const rows = [ cols.join(',') ];
    data.forEach(r => {
      const line = cols.map(c => {
        let v = r[c] ?? '';
        if (typeof v === 'string') v = `"${v.replace(/"/g,'""')}"`;
        return v;
      }).join(',');
      rows.push(line);
    });
    const csv = rows.join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${fileName || 'export'}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  if (loading) return <div className="csv-viewer"><p>Loading…</p></div>;

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={() => nav('/dashboard')}>← Back</button>
        <div className="action-buttons">
          <input
            className="search-input"           
             type="text"
            placeholder="Search…"
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
          <button className="save-btn" onClick={handleSave}>Save Changes</button>
          <button className="logout-btn" onClick={() => { logout(); nav('/login'); }}>Logout</button>
        </div>
      </div>
      <div className="table-wrap">
        <table className="data-table">
            
          <thead>
            <tr>{cols.map(c => <th key={c}>{c}</th>)}</tr>
          </thead>
          <tbody>
            {data.map(r => (
              <tr key={r._rowId} className={r._edited ? 'edited-row' : ''}>
                {cols.map(c => (
                  <td key={c}>
                    <input
                      className="edit-input"
                      value={r[c] ?? ''}
                      onChange={e => handleEdit(r._rowId, c, e.target.value)}
                    />
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
