import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import api from '../api';
import SkeletonTable from './SkeletonTable';
import './Dashboard.css';
import { useTheme } from '../contexts/ThemeContext';

export default function Dashboard() {
  const [files, setFiles]     = useState([]);
  const [loading, setLoading] = useState(true);
   const { logout } = useAuth();
 const { theme, toggleTheme } = useTheme();

  useEffect(() => {
    api.get('/files')
      .then(res => setFiles(res.data))
      .finally(() => setLoading(false));
  }, []);

  const handleDelete = async id => {
    if (!window.confirm('Delete this file?')) return;
    await api.delete(`/files/${id}`);
    setFiles(prev => prev.filter(f => f.id !== id));
  };

  if (loading) {
    // show skeleton 5 rows, 3 cols
    return (
      <div className="dashboard-container">
        <SkeletonTable rows={5} cols={3} />
      </div>
    );
  }

  return (
    <div className="dashboard-container">

         <div className="dashboard-header">
      <h2>Your Files</h2>
      <div>
     <button className="theme-btn" onClick={toggleTheme}>
          {theme === 'light' ? 'üåô Dark' : '‚òÄÔ∏è Light'}
        </button>
        <button className="logout-btn" onClick={logout}>Logout</button>
      </div>
     </div>
      <Link to="/upload"><button className="btn upload-btn">Upload New CSV</button></Link>
      <table className="file-table">
        <thead>
          <tr>
            <th>Filename</th>
            <th>Uploaded At</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {files.map(f => (
            <tr key={f.id}>
              <td><Link to={`/files/${f.id}`}>{f.filename}</Link></td>
              <td>{new Date(f.uploaded_at).toLocaleString()}</td>
              <td>
                <Link to={`/files/${f.id}`}><button className="view-btn">View</button></Link>
                <button className="delete-btn" onClick={() => handleDelete(f.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
