import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import api from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { logout } = useAuth();
  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);

  useEffect(() => {
    api.get(`/files/${id}/data`).then(res => {
      setData(res.data); // res.data is array of { id, col1, col2, ... }
      if (res.data.length) setHeaders(Object.keys(res.data[0]).filter(k => k !== 'id'));
    });
  }, [id]);

  const saveCell = async (rowId, key, value) => {
    // Update local state
    setData(prev => prev.map(row => row.id === rowId ? { ...row, [key]: value } : row));
    // Persist to backend
    const row = data.find(r => r.id === rowId);
    const updatedRow = { ...row, [key]: value };
    await api.put(`/files/${id}/data/${rowId}`, updatedRow);
  };

  if (!data.length) return <p>No data to display.</p>;

  return (
    <div className="csv-viewer">
      <button className="logout-btn" onClick={() => { logout(); navigate('/login'); }}>
        Logout
      </button>
      <table className="data-table">
        <thead>
          <tr>{['Actions', ...headers].map(h => <th key={h}>{h}</th>)}</tr>
        </thead>
        <tbody>
          {data.map(row => (
            <tr key={row.id}>
              <td>
                <button onClick={() => navigate('/dashboard')}>Back</button>
              </td>
              {headers.map(col => (
                <td
                  key={col}
                  contentEditable
                  suppressContentEditableWarning
                  onBlur={e => saveCell(row.id, col, e.target.textContent)}
                >
                  {row[col]}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
