import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import api from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { logout } = useAuth();
  const { addToast } = useToast();

  const [data, setData] = useState([]);      // array of { id, col1, col2, ... }
  const [headers, setHeaders] = useState([]);// list of column keys excluding 'id'
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get(`/files/${id}/data`)
      .then(res => {
        const rows = res.data;
        setData(rows);
        if (rows.length) {
          setHeaders(Object.keys(rows[0]).filter(k => k !== 'id'));
        }
      })
      .catch(() => addToast('Failed to load data', 'error'))
      .finally(() => setLoading(false));
  }, [id, addToast]);

  const handleBack = () => navigate('/dashboard');
  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  // Update a single cell in state
  function handleEdit(rowId, col, value) {
    setData(prev =>
      prev.map(r => (r.id === rowId ? { ...r, [col]: value } : r))
    );
  }

  // Send all updates to server
  async function handleSave() {
    const updates = data.map(({ id, ...rowData }) => ({ id, rowData }));
    try {
      await api.put(`/files/${id}/data`, { updates });
      addToast('Changes saved!', 'success');
    } catch {
      addToast('Save failed. Try again.', 'error');
    }
  }

  if (loading) {
    return <div className="csv-viewer"><p>Loading…</p></div>;
  }

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={handleBack}>← Back</button>
        <button className="save-btn" onClick={handleSave}>Save Changes</button>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </div>

      {data.length ? (
        <table className="data-table">
          <thead>
            <tr>
              {headers.map(h => <th key={h}>{h}</th>)}
            </tr>
          </thead>
          <tbody>
            {data.map(row => (
              <tr key={row.id}>
                {headers.map(col => (
                  <td key={col}>
                    <input
                      className="edit-input"
                      value={row[col] ?? ''}
                      onChange={e => handleEdit(row.id, col, e.target.value)}
                    />
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No data to display.</p>
      )}
    </div>
  );
}
