import React, { useEffect, useState } from 'react'; 
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import api from '../api';
import './Dashboard.css';
import Notifications from './Notifications';

export default function Dashboard() {
  const [files, setFiles] = useState([]);
  const { logout } = useAuth();
  const { addToast } = useToast();

  useEffect(() => {
    api.get('/files').then(res => setFiles(res.data));
  }, []);

  const handleDelete = async id => {
    try {
      await api.delete(`/files/${id}`);
      setFiles(f => f.filter(x => x.id !== id));
      addToast('File deleted!', 'success');
    } catch {
      addToast('Delete failed.', 'error');
    }
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h2>Your Files</h2>
        <Notifications />
        <button 
          className="logout-btn" 
          onClick={() => { logout(); addToast('Logged out','info'); }}
        >
          Logout
        </button>
      </header>

      <div className="upload-container">
        <Link to="/upload" className="upload-btn">
          <span className="upload-icon">ðŸ“¤</span>
          Upload New CSV
        </Link>
      </div>

      <div className="dashboard-grid">
        {files.map(f => (
          <div key={f.id} className="file-card">
            <Link to={`/files/${f.id}`} className="card-link">
              <div className="card-icon">ðŸ“„</div>
              <h3 className="card-title">{f.filename}</h3>
            </Link>
            <p className="card-date">
              {new Date(f.uploaded_at).toLocaleString()}
            </p>
            {f.role === 'collaborator' && (
              <span className="tag-collab">Collaborated</span>
            )}
            <div className="card-actions">
              <Link to={`/files/${f.id}`} className="action-btn view-btn">View</Link>
              <button 
                className="action-btn delete-btn" 
                onClick={() => handleDelete(f.id)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
