import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import api from '../api';
import './CSVViewer.css';

export default function CSVViewer() {
  const { id }       = useParams();
  const nav          = useNavigate();
  const { logout }   = useAuth();
  const { addToast } = useToast();

  const [data, setData]           = useState([]);             // rows w/ _rowId
  const [cols, setCols]           = useState([]);             // CSV column names
  const [searchTerm, setSearchTerm] = useState('');           // global filter
  const [loading, setLoading]     = useState(true);

  // load rows & columns
  useEffect(() => {
    setLoading(true);
    api.get(`/files/${id}/data`)
      .then(res => {
        const rows = res.data;
        setData(rows);
        if (rows.length) {
          setCols(Object.keys(rows[0]).filter(k => k !== '_rowId'));
        }
      })
      .catch(() => addToast('Failed to load data', 'error'))
      .finally(() => setLoading(false));
  }, [id, addToast]);

  // client-side filter
  const filtered = React.useMemo(() => {
    const q = searchTerm.trim().toLowerCase();
    if (!q) return data;
    return data.filter(r =>
      cols.some(c =>
        String(r[c] ?? '').toLowerCase().includes(q)
      )
    );
  }, [searchTerm, data, cols]);

  // inline edit
  const handleEdit = (rowId, col, val) => {
    setData(d => d.map(r => (r._rowId === rowId ? { ...r, [col]: val } : r)));
  };

  // save all edits
  const handleSave = async () => {
    const updates = data.map(r => {
      const { _rowId, ...rowData } = r;
      return { id: _rowId, rowData };
    });
    try {
      await api.put(`/files/${id}/data`, { updates });
      addToast('Changes saved!', 'success');
    } catch {
      addToast('Save failed.', 'error');
    }
  };

  // download CSV
  const handleDownload = () => {
    const lines = [ cols.join(',') ];
    filtered.forEach(r => {
      lines.push(
        cols.map(c => {
          let v = r[c] ?? '';
          if (typeof v === 'string') v = `"${v.replace(/"/g,'""')}"`;
          return v;
        }).join(',')
      );
    });
    const blob = new Blob([lines.join('\n')], { type: 'text/csv' });
    const url  = URL.createObjectURL(blob);
    const a    = document.createElement('a');
    a.href     = url;
    a.download = `export-${id}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // add a new column (client-side)
  const handleAddColumn = () => {
    const name = prompt('New column name:');
    if (!name || cols.includes(name)) return;
    setCols(prev => [...prev, name]);
    setData(prev => prev.map(r => ({ ...r, [name]: '' })));
  };

  // add a new empty row (server-side)
  const handleAddRow = async () => {
    const rowData = cols.reduce((o, c) => ({ ...o, [c]: '' }), {});
    try {
      const res = await api.post(`/files/${id}/data`, { rowData });
      setData(prev => [...prev, { _rowId: res.data.id, ...rowData }]);
    } catch {
      addToast('Failed to add row', 'error');
    }
  };

  if (loading) return <div className="csv-viewer"><p>Loading…</p></div>;

  return (
    <div className="csv-viewer">
      <div className="csv-header">
        <button className="back-btn" onClick={() => nav('/dashboard')}>← Back</button>

        <input
          className="search-input"
          type="text"
          placeholder="Search table…"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />

        <div className="action-buttons">
          <button className="add-column-btn" onClick={handleAddColumn}>
            + Column
          </button>
          <button className="add-row-btn" onClick={handleAddRow}>
            + Row
          </button>
          <button className="download-btn" onClick={handleDownload}>
            Download CSV
          </button>
          <button className="save-btn" onClick={handleSave}>
            Save Changes
          </button>
          <button className="logout-btn" onClick={() => { logout(); nav('/login'); }}>
            Logout
          </button>
        </div>
      </div>

      <div className="table-wrap">
        <table className="data-table">
          <thead>
            <tr>{cols.map(c => <th key={c}>{c}</th>)}</tr>
          </thead>
          <tbody>
            {filtered.length > 0 ? filtered.map(r => (
              <tr key={r._rowId}>
                {cols.map(col => (
                  <td key={col}>
                    <input
                      className="edit-input"
                      value={r[col] ?? ''}
                      onChange={e => handleEdit(r._rowId, col, e.target.value)}
                    />
                  </td>
                ))}
              </tr>
            )) : (
              <tr>
                <td colSpan={cols.length} className="no-data">
                  No matching rows.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
